import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { FormControl } from 'react-bootstrap';
import './calendar-event-form.css';

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
});

class CalendarEventForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: '',
      start: '',
      end: ''
    };

    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.setState({
      start: this.props.start,
      end: this.props.end,
    })
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }

  handleSubmit(e) {
    // need to write validation method
    e.preventDefault();
    this.props.history.push(`/`);
  }

  render() {
    const { classes } = this.props;
    return (
      <div className="form-event-container">
      <form className={classes.container} onSubmit={this.handleSubmit} noValidate>

        {/* Title Form*/}
        <FormControl
          type="text"
          value={this.state.value}
          placeholder="Enter title of Event"
          onChange={this.handleChange}
        />

        {/* Text Area */}
        <FormControl
          componentClass="textarea"
          placeholder="Description"
        />

        {/* Start Time and Date */}
        <TextField
          id="datetime-local"
          label="Start"
          type="datetime-local"
          defaultValue="2017-05-24T10:30"
          className={classes.textField}
          InputLabelProps={{
            shrink: true,
          }}
        />

        {/* Start Time and Date */}
        <TextField
          id="datetime-local"
          label="End"
          type="datetime-local"
          defaultValue="2017-05-24T10:30"
          className={classes.textField}
          InputLabelProps={{
            shrink: true,
          }}
        />

        <input type="submit" value="Submit" />
      </form>
      </div>


    )
  }
}

export default withStyles(styles)(CalendarEventForm);
