import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { Form, FormControl, FieldGroup } from 'react-bootstrap';

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
});


class CalendarEventForm extends Component {

  render() {
    const { classes } = this.props;
    return (
      <form className={classes.container} noValidate>

        {/* Title Form*/}
        <FieldGroup
          id="formControlsText"
          type="text"
          label="Title"
          placeholder="Enter title of Event"
        />

        {/* Text Area */}
        <FormControl componentClass="textarea" placeholder="textarea" />

        {/* Start Time and Date */}
        <TextField
          id="datetime-local"
          label="Start"
          type="datetime-local"
          defaultValue="2017-05-24T10:30"
          className={classes.textField}
          InputLabelProps={{
            shrink: true,
          }}
        />

        {/* Start Time and Date */}
        <TextField
          id="datetime-local"
          label="End"
          type="datetime-local"
          defaultValue="2017-05-24T10:30"
          className={classes.textField}
          InputLabelProps={{
            shrink: true,
          }}
        />


      </form>


    )
  }
}

export default withStyles(styles)(CalendarEventForm);
