const eventSchema =  {
  title: 'event schema',
  version: 0,
  description: 'describes a calendar event',
  type: 'object',
  properties: {
    "summary": {
      'type': 'string',
      'primary': true
    },
    "end": {
      "type": "object",
      "properties": {
          "dateTime": {
              "type": "string"
          },
          "timezone": {
              "type": "number"
          }
      }
    },
    "start": {
      "type": "object",
      "properties": {
          "dateTime": {
              "type": "string"
          },
          "timezone": {
              "type": "number"
          }
      }
    },
    "allDay": {
      'type': 'boolean'
    }
  },
  required: ['summary', 'end', 'start']
}

export default eventSchema;
