import {
  UPDATE_STORED_EVENTS
} from '../actions/events';
import {
  SUCCESS_STORED_EVENTS
} from '../actions/db/events';

const initialState = {
  calEvents: {},
  calEventIds: []
}

const mergeEvents = (oldEvents, newData, newIds) => {
  let newEvents = newData.normalData.entities.events;
  let updatedEventList = [...oldEvents];
  for(let key of newIds) {
    if(!oldEvents.hasOwnProperty(key)) {
      updatedEventList[key] = newEvents[key];
    }
  }
  let updatedEventsList = [...oldEvents, newPayload]
  return updatedEventsList
}

const mergeIds = (oldIds, newIds) => {
  for(let key of newIds) {
    if(!oldIds.includes(key)) {
      oldIds.push(key);
    }
  }
  return oldIds;
}

export default function eventsReducer(state = initialState, action) {
  switch(action.type) {
    case UPDATE_STORED_EVENTS:
      return Object.assign({}, state, { calEvents: action.payload });
    case SUCCESS_STORED_EVENTS: {
      let newEventsIds = mergeIds(state.calEventsIds, action.payload.result.events);
      let newEvents = mergeEvents(state.calEvents, action.payload, newEventsIds)
      return Object.assign({}, state, { calEvents: newEvents, calEventIds: newEventsIds })
    }
    default:
      return state
  }
}
