/*
 * action types
 */
export const ADD_NEW_EVENT = 'ADD_NEW_EVENT';
export const MOVE_EVENT = 'MOVE_EVENT';

export function addNewEvent(newEvent) {
  return {
    type: ADD_NEW_EVENT,
    payload: {
      newEvent: newEvent
    }
  }
}

export function moveEvent(updateEvents) {
  return {
    type: MOVE_EVENT,
    payload: {
      updateEvents: updateEvents
    }
  }
}
