import { createSelector } from 'reselect'

const getGoogleEvents = state => state.google_data;
const getEvents = state => state.events;


//process google events data
export const getFilteredEvents = createSelector(
  [getGoogleEvents, getEvents],
  (google_events, events) => {
    setFormat(google_events)
    return events
  }
)

function setFormat(events) {
  const formated_events = events.map(eachEvent => {
    title: eachEvent.summary
  });

  return formated_events;
}
