import { GET_EVENTS_BEGIN } from '../actions/events';
import { duplicateAction } from '../actions/db/events';
import { map, mergeMap, catchError } from 'rxjs/operators';
import { ofType } from 'redux-observable';
import { from } from 'rxjs';
import { normalize, schema } from 'normalizr';


export const beginGetEventsEpics = action$ => action$.pipe(
  ofType(GET_EVENTS_BEGIN),
  map(() => {
    getEvents().then(resp => {
      debugger;
      return duplicateAction();
    })
  })
)

const getEvents = async () => {
  window.gapi.client.load('calendar', 'v3')
    .then(() => {
      let syncToken = localStorage.getItem('sync');
      if(syncToken == null) {
        console.log('performing full sync');
        return window.gapi.client.calendar.events.list({
          'calendarId' : 'primary'
        });
      }
      else {
        console.log('performing incremental sync');
        return window.gapi.client.calendar.events.list({
          'calendarId' : 'primary',
          'syncToken': syncToken
        });
      }
    })
    .then(resp => {
      debugger
      let result = [];
      return new Promise((resolve, reject) => {
        fetchEvents(resp, result, resolve, reject);
      })
    })
    .then(async response => {
      const myData = { events : response};
      const singleEvent = new schema.Entity('events');
      const mySchema = { events: [ singleEvent ]};
      return normalize(myData, mySchema);
    })
}

