import {
  UPDATE_STORED_EVENTS
} from '../actions/events';
import {
  SUCCESS_STORED_EVENTS
} from '../actions/db/events';

const initialState = {
  calEvents: [],
}

const mergeEvents = (oldEvents, newData, newItems) => {
  debugger
  let oldIds = oldEvents.map(item => item.id);
  let newEvents = newData.normalData.entities.events;
  let newIds = [newData.normalData.entities.results.events];
  let newPayload = [...oldEvents];
  for(let newItem of newItems) {
    if(!oldIds.includes(newItem)) {
      newPayload.push(newItem)
    }
  }
  return newPayload;
}

export default function eventsReducer(state = initialState, action) {
  switch(action.type) {
    case UPDATE_STORED_EVENTS:
      return Object.assign({}, state, { calEvents: action.payload });
    case SUCCESS_STORED_EVENTS: {
      let newEvents = mergeEvents(state.calEvents, action.payload, action.item)
      return Object.assign({}, state, { calEvents: newEvents })
    }
    default:
      return state
  }
}
