const eventSchema =  {
  title: 'event schema',
  version: 0,
  description: 'describes a calendar event',
  type: 'object',
  properties: {
    id: {
      'type': 'string',
      'primary': true
    },
    title: {
      'type': 'string'
    },
    end: {
      'type': 'number' //datetime
    },
    start: {
      'type': 'number' //datetime
    },
    allDay: {
      'type': 'boolean'
    }
  },
  required: ['title', 'end', 'start']
}

export default eventSchema;
