import React from "react";
import { DragDropContext } from "react-dnd";
import BigCalendar from "react-big-calendar";
import HTML5Backend from "react-dnd-html5-backend";
import withDragAndDrop from "react-big-calendar/lib/addons/dragAndDrop";
import moment from "moment";

const localizer = BigCalendar.momentLocalizer(moment);
const DragAndDropCalendar = withDragAndDrop(BigCalendar);

class ReactCalendarView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      events: [
        {
          allDay: false,
          end: new Date('December 10, 2018 11:13:00'),
          start: new Date('December 09, 2018 12:13:00'),
          title: 'hi',
        },
        {
          allDay: true,
          end: new Date('December 09, 2018 11:13:00'),
          start: new Date('December 09, 2018 11:13:00'),
          title: 'All Day Event',
        }
      ]
    };

    this.moveEvent = this.moveEvent.bind(this);
  }

  moveEvent({ event, start, end }) {
      const { events } = this.state;

      const idx = events.indexOf(event);
      const updatedEvent = { ...event, start, end };

      const nextEvents = [...events];
      nextEvents.splice(idx, 1, updatedEvent);

      this.setState({
        events: nextEvents
      });
  }

  resizeEvent = (resizeType, { event, start, end }) => {
    const { events } = this.state;

    const nextEvents = events.map(existingEvent => {
      return existingEvent.id === event.id
        ? { ...existingEvent, start, end }
        : existingEvent;
    });

    this.setState({
      events: nextEvents
    });
  };

  render() {
    return (
      <DragAndDropCalendar
        selectable
        events={this.state.events}
        onEventDrop={this.moveEvent}
        resizable
        onEventResize={this.resizeEvent}
        defaultView={BigCalendar.Views.MONTH}
        defaultDate={new Date(2015, 3, 12)}
      />
    );
  }
}

export const Calendar = DragDropContext(HTML5Backend)(ReactCalendarView);
