export const RETRIEVE_STORED_EVENTS = 'RETRIEVE_EVENTS';
export const GET_EVENTS_BEGIN = 'GET_EVENTS_BEGIN';
export const GET_EVENTS_SUCCESS = 'GET_EVENTS_SUCCESS';
export const GET_EVENTS_FAILURE = 'GET_EVENTS_FAILURE';
export const POST_EVENT_BEGIN = 'POST_EVENT_BEGIN';
export const POST_EVENT_SUCCESS = 'POST_EVENT_SUCCESS';
export const POST_EVENT_FAILURE = 'POST_EVENT_FAILURE';
export const UPDATE_STORED_EVENTS = 'UPDATE_EVENTS';


export const beginGetGoogleEvents = () => ({ type: GET_EVENTS_BEGIN })

export const retrieveStoreEvents = () => ({ type: RETRIEVE_STORED_EVENTS })

export const updateStoredEvents = (updatedEvents) => ({
    type: UPDATE_STORED_EVENTS,
    payload: updatedEvents
})

export const postGoogleEvent = (calEvent) => ({
  type: POST_EVENT_BEGIN,
  payload: calEvent
})
