import { combineEpics } from 'redux-observable';
import {
  retrieveEventsEpic,
  storeEventsEpic,
  beginStoreEventsEpic,
} from './db/events';
import {
  beginGoogleAuthEpic
} from './events';

export const rootEpic = combineEpics(
  beginGoogleAuthEpic,
  retrieveEventsEpic,
  storeEventsEpic,
  beginStoreEventsEpic,
);
