/*
 * action types
 */
export const ADD_NEW_EVENT = 'ADD_NEW_EVENT';
export const MOVE_EVENT = 'MOVE_EVENT';
export const RESIZE_EVENT = 'RESIZE_EVENT';

export function addNewEvent(newEvent) {
  return {
    type: ADD_NEW_EVENT,
    payload: {
      newEvent: newEvent
    }
  }
}

export function moveEvent(updatedEvents) {
  return {
    type: MOVE_EVENT,
    payload: {
      updatedEvents: updatedEvents
    }
  }
}

export function resizeEvent(updatedEvents) {
  return {
    type: RESIZE_EVENT,
    payload: {
      updatedEvents: updatedEvents
    }
  }
}
