import React, { Component } from 'react';
import { withStyles, MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { FormControl } from 'react-bootstrap';
import './calendar-event-form.css';
import { withRouter } from 'react-router-dom';
import purple from '@material-ui/core/colors/purple';
import green from '@material-ui/core/colors/green';
import InputLabel from '@material-ui/core/InputLabel';
import moment from "moment";




const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 300,
  },
  margin: {
    margin: theme.spacing.unit,
  },
  cssLabel: {
    '&$cssFocused': {
      color: purple[500],
    },
  },
  cssFocused: {},
});

const theme = createMuiTheme({
  palette: {
    primary: green,
  },
  typography: { useNextVariants: true },
});

class Form extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: '',
      start: '2017-05-24T10:30',
      end: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleChangeTime = this.handleChangeTime.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    const startDateParsed = moment(this.props.match.params.start).format("YYYY-MM-DD")
    const endDateParsed = moment(this.props.match.params.end).format("YYYY-MM-DD");
    this.setState({
      start: startDateParsed + 'T10:30',
      end: endDateParsed + 'T10:30'
    })
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }

  handleChangeTime(e) {
    this.setState({ start: e.target.value });
  }

  handleSubmit(e) {
    // need to write validation method
    e.preventDefault();
    this.props.history.push(`/`);
  }

  render() {
    const { classes } = this.props;
    return (
      <div className="form-event-container">
      <form className={classes.container} onSubmit={this.handleSubmit} noValidate>

        {/* Title Form*/}
        <FormControl
          type="text"
          value={this.state.value}
          placeholder="Enter title of Event"
          onChange={this.handleChange}
        />

        {/* Text Area */}
        <FormControl
          componentClass="textarea"
          placeholder="Description"
          onChange={this.handleChange}
        />

        {/* Start Time and Date */}
        <TextField
          id="datetime-local"
          label="Start"
          type="datetime-local"
          defaultValue={this.state.start}
          className={classes.textField}
          InputLabelProps={{
            shrink: true,
          }}
          onChange={this.handleChangeTime}
        />

        {/* End Time and Date */}
        <TextField
          id="datetime-local"
          label="End"
          type="datetime-local"
          defaultValue={this.state.end}
          className={classes.textField}
          InputLabelProps={{
            shrink: true,
          }}
        />

        <input type="submit" value="Submit" />
      </form>
      </div>


    )
  }
}

const CalendarEventForm = withStyles(styles)(Form)

export default withRouter(CalendarEventForm);
