/*
 * action types
 */
export const ADD_NEW_EVENT = 'ADD_NEW_EVENT';
export const UPDATE_EVENTS = 'UPDATE_EVENTS';

export function addNewEvent(newEvent) {
  return {
    type: ADD_NEW_EVENT,
    payload: {
      newEvent: newEvent
    }
  }
}
export function updateEvents(updatedEvents) {
  return {
    type: UPDATE_EVENTS,
    payload: {
      updatedEvents: updatedEvents
    }
  }
}
