const eventSchema =  {
  title: 'event schema',
  version: 0,
  description: 'describes a calendar event',
  type: 'object',
  properties: {
    summary: {
      'type': 'string',
      'primary': true
    },
    end: {
       'dateTime': {
         'type': 'string'
       },
       'timezone' : {
         'type': 'string'
       }

    },
    start: {
       'dateTime': {
         'type': 'string'
       },
       'timezone' : {
         'type': 'string'
       }
    },
    allDay: {
      'type': 'boolean'
    }
  },
  required: ['summary', 'end', 'start']
}

export default eventSchema;
