import { map, catchError } from 'rxjs/operators';
import { ofType } from 'redux-observable';
import { from } from 'rxjs';
import { API_KEY, GOOGLE_CLIENT_ID, GOOGLE_SCOPE } from '../utils/google';
import {
  BEGIN_GOOGLE_AUTH,
  successGoogleAuth,
  failGoogleAuth
} from './actions/auth';

let GoogleAuth = '';

const handleAuthClick = (auth) => {
  if(auth.isSignedIn.get()) {
    console.log("Signed In to Google!");
  }
  else {
    auth.signIn();
  }
}

const loginGoogle = () => {
  debugger
  window.gapi.load('client:auth2', {
    callback: () => {
      window.gapi.client.init({
          'apiKey': API_KEY,
          'clientId': GOOGLE_CLIENT_ID,
          'scope': GOOGLE_SCOPE,
          'discoveryDocs': ['https://www.googleapis.com/discovery/v1/apis/drive/v3/rest'],
      }).then(() => {
          GoogleAuth = window.gapi.auth2.getAuthInstance();
          //GoogleAuth.signIn();
          handleAuthClick(GoogleAuth);
          const user = GoogleAuth.currentUser.get();
          const isAuthorized = user.hasGrantedScopes(GOOGLE_SCOPE);
          return isAuthorized;
      })
    }
  });
}

export const beginGoogleAuthEpic = action$ => action$.pipe(
  ofType(BEGIN_GOOGLE_AUTH),
  map((user) => (loginGoogle()) ? successGoogleAuth(user) : failGoogleAuth())
)
