/*
 * action types
 */
export const ADD_NEW_EVENT = 'ADD_NEW_EVENT';
export const MOVE_EVENT = 'MOVE_EVENT';
export const UPDATE_EVENT = 'UPDATE_EVENT';

export function addNewEvent(newEvent) {
  return {
    type: ADD_NEW_EVENT,
    payload: {
      newEvent: newEvent
    }
  }
}
export function updateEvent(updatedEvents) {
  return {
    type: RESIZE_EVENT,
    payload: {
      updatedEvents: updatedEvents
    }
  }
}
