import {
  BEGIN_GOOGLE_AUTH,
  successGoogleAuth,
  failGoogleAuth
} from '../actions/auth';

let GoogleAuth;

const handleAuthClick = () => {
  if(GoogleAuth.isSignedIn.get()) {
    console.log("Signed In to Google!");
  }
  else {
    GoogleAuth.signIn();
  }
}

const loginGoogle = () => {
  return window.gapi.load('client:auth2', initClient);
}

