import { ADD_NEW_EVENT, MOVE_EVENT } from './actions';

const initialState = {
  events: [
    /*{
      allDay: false,
      end: new Date('December 10, 2018 11:13:00'),
      start: new Date('December 09, 2018 12:13:00'),
      title: 'hi',
    }*/
    {
      allDay: false,
      end: new Date('December 12, 2018 02:13:00'),
      start: new Date('December 12, 2018 01:13:00'),
      title: 'New Event',
    }
  ]
}


export default function eventsReducer(state = initialState, action) {
  switch(action.type) {
    case ADD_NEW_EVENT:
      const newEventsList = state.events.concat(action.payload.newEvent);
      return {
        events: newEventsList
      }
    case MOVE_EVENT:
      return {
        events: action.payload.updatedEvents
      }
    default:
      return state;
  }
}
