const CALENDAR_ID = 'shamsheer619@gmail.com';
const CLIENT_ID = '65724758895-j8e7e5ovpujg8r32mg36v9pevmsbh9d2.apps.googleusercontent.com';
const API_KEY = 'AIzaSyCTYXWtoRKnXeZkPCcZwYOXm0Qz3Lz9F9g';
const URL = `https://www.googleapis.com/calendar/v3/calendars/events`;

function initClient() {
  debugger
  window.gapi.client.init({
      'apiKey': API_KEY,
      'clientId': CLIENT_ID,
      'scope': 'https://www.googleapis.com/auth/calendar.events',
      'discoveryDocs': ['https://www.googleapis.com/discovery/v1/apis/drive/v3/rest']
  }).then(function () {
      const GoogleAuth = window.gapi.auth2.getAuthInstance();
      // Listen for sign-in state changes.
  });
}

export const apiMiddleware = store => next => action => {
  if(action.type === 'GET_EVENTS') {
    debugger
    initClient();
  }
  return next(action);
}
