import { BEGIN_GET_GOOGLE_CALENDAR, successGetGoogleCalendar, failGetGoogleCalendar } from '../redux/actions';
import { mergeMap, map } from 'rxjs/operators';
import { ofType } from 'redux-observable';


export const getGoogleCalendarEpic = action$ => action$.pipe(
  ofType(BEGIN_GET_GOOGLE_CALENDAR),
  mergeMap(action =>
    window.gapi.client.request({
         'path': `https://www.googleapis.com/calendar/v3/calendars/primary/events`,
    }).pipe(
      map(resp => successGetGoogleCalendar(response)),
      catchError(error => failGetGoogleCalendar(error))
    )
  )
);

window.gapi.client.request({
     'path': `https://www.googleapis.com/calendar/v3/calendars/primary/events`,
   }).then(resp => {
   let events = resp.result.items;
   next({
     type: action.type + '_SUCCESS',
     payload: {
       data: events
     }
   });
 }, (reason) => {
   next({
     type: action.type + '_FAILURE',
     payload: {
       data: reason
     }
   });
 });
